import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {

    state = { mouse: false };

    handleMouse = (flag) => {
        return () => {
            this.setState({ mouse: flag });
        }
    }

    handleCheck = (id) => {
        return (event) => {
            this.props.updateTodo(id, event.target.checked);
        }

    }

    render() {
        return (
            <li>
                <label>
                    <input type="checkbox" />
                    <span>****</span>
                </label>
                <button className="btn btn-danger">删除</button>
            </li>
        )
    }
}
